Scripts.OnLoad:
UIDropDownMenu_Initialize(CooldownTimersOptionsTextures, function()
local tex1 = { text = CDT_TEXTURES_STANDARD,
			  value = 1,
			  func = function()
				UIDropDownMenu_SetSelectedValue(CooldownTimersOptionsTextures, 1);
			  end }

local tex2 = { text = CDT_TEXTURES_CILO,
			  value = 2,
			  func = function()
				UIDropDownMenu_SetSelectedValue(CooldownTimersOptionsTextures, 2);
			  end }

local tex3 = { text = CDT_TEXTURES_SMOOTH,
			  value = 3,
			  func = function()
				UIDropDownMenu_SetSelectedValue(CooldownTimersOptionsTextures, 3);
			  end }

UIDropDownMenu_AddButton(tex1);
UIDropDownMenu_AddButton(tex2);
UIDropDownMenu_AddButton(tex3);
end);
UIDropDownMenu_SetWidth(120, CooldownTimersOptionsTextures);

Scripts.OnUpdate:
if( CooldownTimersOptionsHorizontalSlider8Slider:GetValue() ~= 2 ) then
	CooldownTimersOptionsLabel14Label:SetText( string.format( "%ds", CooldownTimersOptionsHorizontalSlider8Slider:GetValue() ) );
else
	CooldownTimersOptionsLabel14Label:SetText( CDT_ALWAYS );
end
